import PropTypes from "prop-types";
import "./style.css";

const Item = (props) => {
  const { name, money } = props;

  const status = money < 0 ? "expense" : "income";

  return (
    <span>
      <li className={status}>
        {name}
        <span> &nbsp; {money}</span>
      </li>
    </span>
  );
};

Item.propTypes = {
  name: PropTypes.string.isRequired,
  money: PropTypes.number.isRequired,
};

export default Item;
