import './App.css'
import React, { useState } from 'react';
import Title from './component/Title';
import Form from './component/form';


const App = () =>{

    const initData = [
        {id:1, name:"nutthanon", money:1000},
        {id:2, name:'josave', money:500},
        {id:3, name:"Josave", money:200}
    ]

    const [items, setItems] = useState(initData)

    const onAddNewItem = (newItem) =>{
        setItems((prev)=>{
            return [newItem, ...prev]
        })
    }

    return(
        <div>
            <h1>Hello</h1>
            <Form onAddItem={onAddNewItem}/>
            <Title items={items}/>
        </div>
    )
}


export default App;
