import "./App.css";
import React, { useState } from "react";
import Title from "./component/Title";
import Form from "./component/form";
import "./App.css";

const App = () => {
  const [items, setItems] = useState([]);

  const onAddNewItem = (newItem) => {
    setItems((prev) => {
      return [newItem, ...prev];
    });
  };

  return (
    <div>
      <h1>Hello</h1>
      <Form onAddItem={onAddNewItem} />
      <Title items={items} />
    </div>
  );
};

export default App;
